mapper.select:
SELECT p.*, pt.type as tag_type, (6371 * acos( cos(radians(#{y})) * cos(radians(p.y)) * cos(radians(p.x) - radians(#{x})) + sin(radians(#{y})) * sin(radians(p.y)) )) AS distance FROM places p LEFT JOIN place_tags pt ON p.tag = pt.id
AND p.tag = #{tag}
HAVING distance <= #{dist} ORDER BY distance

SELECT p.*, pt.type as tag_type, (6371 * acos( cos(radians(#{y})) * cos(radians(p.y)) * cos(radians(p.x) - radians(#{x})) + sin(radians(#{y})) * sin(radians(p.y)) )) AS distance FROM places p LEFT JOIN place_tags pt ON p.tag = pt.id
AND p.tag = #{tag}
HAVING distance <= #{dist} ORDER BY distance LIMIT 15

SELECT p.*, pt.type as tag_type, CASE WHEN p.name = #{keyword} THEN 100 WHEN pt.type LIKE CONCAT('%', #{keyword}, '%') THEN 70 WHEN p.name LIKE CONCAT(#{keyword}, '%') THEN 50 WHEN p.name LIKE CONCAT('%', #{keyword}, '%') THEN 30 WHEN p.address LIKE CONCAT('%', #{keyword}, '%') THEN 10 ELSE 0 END as relevance_score FROM places p LEFT JOIN place_tags pt ON p.tag = pt.id WHERE (p.name LIKE CONCAT('%', #{keyword}, '%') OR p.address LIKE CONCAT('%', #{keyword}, '%') OR pt.type LIKE CONCAT('%', #{keyword}, '%'))
AND p.tag = #{tag}
ORDER BY relevance_score DESC, p.average_rating DESC LIMIT 1000

SELECT p.*, pt.type as tag_type, CASE WHEN p.name = #{keyword} THEN 100 WHEN pt.type LIKE CONCAT('%', #{keyword}, '%') THEN 70 WHEN p.name LIKE CONCAT(#{keyword}, '%') THEN 50 WHEN p.name LIKE CONCAT('%', #{keyword}, '%') THEN 30 WHEN p.address LIKE CONCAT('%', #{keyword}, '%') THEN 10 ELSE 0 END as relevance_score, (6371 * acos( cos(radians(#{y})) * cos(radians(p.y)) * cos(radians(p.x) - radians(#{x})) + sin(radians(#{y})) * sin(radians(p.y)) )) AS distance FROM places p LEFT JOIN place_tags pt ON p.tag = pt.id WHERE (p.name LIKE CONCAT('%', #{keyword}, '%') OR p.address LIKE CONCAT('%', #{keyword}, '%') OR pt.type LIKE CONCAT('%', #{keyword}, '%'))
AND p.tag = #{tag}
HAVING distance <= #{dist} ORDER BY relevance_score DESC, distance ASC LIMIT 1000

mapper.update:
UPDATE places
SET average_rating = #{averageRating}
WHERE id = #{id}
