mapper.select:
SELECT r.*, rt.type as tag_type, (6371 * acos( cos(radians(#{y})) * cos(radians(r.y)) * cos(radians(r.x) - radians(#{x})) + sin(radians(#{y})) * sin(radians(r.y)) )) AS distance FROM restaurants r LEFT JOIN restaurant_tags rt ON r.tag = rt.id
AND r.tag = #{tag}
HAVING distance <= #{dist} ORDER BY distance

SELECT r.*, rt.type as tag_type FROM restaurants r LEFT JOIN restaurant_tags rt ON r.tag = rt.id WHERE MATCH(r.name, r.address) AGAINST(#{keyword} IN BOOLEAN MODE)
AND r.tag = #{tag}
LIMIT 1000

mapper.update:
UPDATE restaurants
SET average_rating = #{averageRating}
WHERE id = #{id}
